
public class ArithmeticOperator {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=60,b=20,c1;
		c1=a+b;
		System.out.println("addition="+c1);
		
//		c1=a-b; 
		int c2=a-b;	//reinitialization								
		System.out.println("substraction="+c2);
	
	    int c3=a*b;
		System.out.println("multiplication="+c3);

		float c4=a/b;
		System.out.println("division="+c4);
		
		float c5=a%b;
		System.out.println("modulas="+c5);
		
		char e='A',f='B';
		int g=e+f;
		System.out.println("addition="+g);


	}

}
